const convertToTree = (list = []) => {
    const res = []

    let itemMap = {}
    list.forEach(item => {
        itemMap[item.id] = { ...item, children: [] }
    })

    list.forEach(item => {
        const treeItem = itemMap[item.id]; //
        const pItem = itemMap[item.parentId]; // 

        if (pItem) {
            pItem.children.push(treeItem)
        } else {
            // 
            res.push(treeItem)
        }
    })
    return res
}

convertToTree(list)
