                    string error = p.StandardError.ReadToEnd();
                    //
                    p.WaitForExit();
                    res.ToRes(output, error);
                }

                catch (System.Exception e)
                {
                    System.Console.WriteLine(e.Message);
                }
                finally
                {
                    p.Close();
                    p.Dispose();
                }
                return res;
            }
        }
