'use strict';
const tcb = require('tcb-admin-node');
const app = tcb.init({ env: 'ID' });
const db = app.database();
const articleDb = db.collection('article');

exports.main = async (event) => {
    try {
        let { hash, url } = event;
        // hash  article id
        let { data } = await articleDb.field({ _id: 1 }).where({ hash }).get();
        let articleID = '';
        if (data.length === 0) {
            //   article 
            let { id } = await articleDb.add({ date: new Date(), url, hash, })
            articleID = id;
        } else {
            let { _id } = data[0]
            articleID = _id;
        }

        return {
            success: true,
            data: articleID
        }
    } catch (error) {
        return {
            success: false,
            data: error
        }
    }
}
