class _AnimatedDemo extends AnimatedWidgetBaseState<AnimatedDemo> {
  ColorTween _color;
  Tween<double> _height;

  //  build
  @override
  Widget build(BuildContext context) {
    return Container(
      color: _color.evaluate(animation), //evaluateTween
      height: _height.evaluate(animation),
      child: widget.child,
    );
  }

  //buildTween
  @override
  void forEachTween(visitor) {
    //visitor tweenTween
    _color = visitor(_color, widget.color, (value) => ColorTween(begin: value));// value==widget.color
