// ImplicitlyAnimatedWidget 
class AnimatedDemo extends ImplicitlyAnimatedWidget {
  final Color color;
  final Widget child;
  final double height;

  AnimatedDemo({
    this.color,
    this.height,
    Curve curve = Curves.linear,
    this.child,
    @required Duration duration,
  }) : super(curve: curve, duration: duration);


  @override
  _AnimatedDemo createState() => _AnimatedDemo();
}
//ImplicitlyAnimatedWidget StatefulWidget  AnimatedWidgetBaseState  ImplicitlyAnimatedWidgetState
class _AnimatedDemo extends AnimatedWidgetBaseState<AnimatedDemo> {
  ColorTween _color;
  Tween<double> _height;

  //  build
  @override
  Widget build(BuildContext context) {
    return Container(
      color: _color.evaluate(animation), //evaluateTween
      height: _height.evaluate(animation),
      child: widget.child,
    );
  }

  //buildTween
  @override
  void forEachTween(visitor) {
    //visitor tweenTween
    _color = visitor(_color, widget.color, (value) => ColorTween(begin: value));// value==widget.color
    _height = visitor(_height, widget.height, (value) => Tween<double>(begin: value));
  }
}
